import React, { useState, useEffect } from 'react';
import { Routes, Route, BrowserRouter } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Home from './components/Home';
import handlerInp from './handler.js'
import ChitChat from './components/ChitChat.js';


//const handler = require('./handler.js');

function App() {
  // used to navigate to other pages
  //let navigate = useNavigate();
  // create a dataset for login
  const [signInData, setSignInData] = useState({
    username: "",
    password: "",
    usernameValid: false,
    passwordValid: false,
    rememberCheckbox: false
  })

  const [signUpData, setSignUpData] = useState({
    firstname: "",
    lastname: "",
    username: "",
    email: "",
    password: "",
    emailValid: false,
    usernameValid: false,
    passwordValid: false
  })

  const [homeData, setHomeData] = useState({
    email: ""
  })

  // Handle Sign In
  const signIn = e => {
    handlerInp.handleSignInInputChange(e, signInData, setSignInData);
  }

  // Sign Up Input Handler
  const signUp = (e) => {
    handlerInp.handleSignUpInputChange(e, signUpData, setSignUpData);
  }

  const generateSecretKey = () => {
    let secret = (Math.random() + 1).toString(36).substring(2);
    return secret
  }

  // add user to Chat Engine
  const addUserChatEngine = (data) => {
    var data = {
      "username": data.username,
      "secret": generateSecretKey(),
      "email": data.email,
      "first_name": data.firstname,
      "last_name": data.lastname
    };
    var config = {
      method: 'post',
      url: 'https://api.chatengine.io/users/',
      headers: {
        'PRIVATE-KEY': 'abc28124-8812-4af4-a446-ad31203abfbe'
      },
      data : data
    };
  }

  // Handle Sign Up
  const signUpHandler = (event) => {
      event.preventDefault();
      console.log("Uploading info to database...")
      fetch("/Register/posts", {
        method: "POST",
        mode: "cors", 
        //redirect: 'follow',
        body: JSON.stringify(signUpData),
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
      .then(response => response.json())
      .then(data => setSignUpData([data]))
      .then(data => console.log([data]))
      .then(msg => console.log("Message: ", msg))
      .catch(error => console.log(error))


      console.log("Fetching done")
      // clear the inputs
      setSignUpData({
          firstname: "",
          lastname: "",
          email: "",
          username: "",
          password: "",
          emailValid: false,
          usernameValid: false,
          passwordValid: false
      });
  }



  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route index element={<Login data={signInData} onChange={signIn}/>} />
          <Route path='/login' element={<Login data={signInData} onChange={signIn}/>}/>
          <Route path="/register" element={<Register data={signUpData} onChange={signUp} handleSignUp={signUpHandler}/>} />
          <Route path='/home' element={<Home/>}/>
          <Route path='/chitchat' element={<ChitChat/>}/>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
